import  React,{useState,useEffect} from 'react';
import Grid from '@mui/material/Grid';
import IconButton from '@mui/material/IconButton';
import CallIcon from '@mui/icons-material/Call';
import bgimage from '../images/person.jpeg';
import '../ComponentStyle/Host.css';

const UserHost =()=> {
  // const [ws, setWs] = useState(null);
  // const [timer, setTimer] = useState(null);
  // const [countdown, setCountdown] = useState(null);

  // useEffect(() => {
  //   const newWs = new WebSocket('ws://localhost:8080');

  //   newWs.onopen = function() {
  //     console.log('Connected to server');
  //   };

  //   newWs.onmessage = function(event) {
  //     if (event.data === 'timer-finished') {
  //       setCountdown(null);
  //       playSound();
  //     } else {
  //       setCountdown(parseInt(event.data));
  //     }
  //   };

  //   setWs(newWs);

  //   return () => {
  //     newWs.close();
  //   };
  // }, []);

  // const startCountdown = (seconds) => {
  //   if (ws) {
  //     ws.send('start-countdown');

  //     if (seconds) {
  //       ws.send(`set-timer:${seconds}`);
  //     }
  //   }
  // };

  // const playSound = () => {
  //   const audio = new Audio('sound.mp3');
  //   audio.play();
  // };

  // const handleTimerClick = (seconds) => {
  //   setTimer(seconds);
  //   setCountdown(null);
  //   startCountdown(seconds);
  // };

  const [timer, setTimer] = useState('');
  const [ws, setWs] = useState(null);

  const connectWebSocket = () => {
    const newWs = new WebSocket('ws://localhost:8080');
    setWs(newWs);

    newWs.onmessage = (event) => {
      const message = event.data;
      if (message === 'done') {
        setTimer('Time is up!');
      } else {
        setTimer(message);
      }
    };
  };

  const startTimer = (seconds) => {
    if (ws) {
      ws.send(seconds.toString());
    }
  };
    const buttons = [
      <IconButton key="1" aria-label="call" color="primary" style={{ backgroundColor: '#c4c4c4', borderRadius: '50%' ,margin: '10px'}} onClick={() => startTimer(45)}>45s{timer}</IconButton>,
      <IconButton key="2" aria-label="call" color="primary" style={{ backgroundColor: '#c4c4c4', borderRadius: '50%' ,margin: '10px'}} onClick={() => startTimer(30)}>30s{timer}</IconButton>,
      <IconButton key="3" aria-label="call" color="primary" style={{ backgroundColor: '#c4c4c4', borderRadius: '50%' ,margin: '10px'}} onClick={() => startTimer(15)}>15s{timer}</IconButton>,

    ];
  return (
    <Grid container style={{ height: '100vh' }}>
    <Grid container>
    {/* // color match krwana h */}
      <Grid item xs={2} style={{ background: '#fff', height: '100%',border: '1px solid white'}}> 
        <Grid item className='sidegrid' >
          <div className='participant'>Participant1</div>
        </Grid>
        <Grid item  className='sidegrid' >
          <div className='participant'>Participant2</div>
        </Grid>
        <Grid item  className='sidegrid' >
          <div className='participant'>Participant3</div>
        </Grid>
      </Grid>
      <Grid item xs={10} className='maingrid'>
        <Grid container style={{ height: '100%' }}>
          <Grid item xs={12} style={{ display: 'flex', justifyContent: 'center', alignItems: 'flex-end', marginBottom: 20 }}>
            {/* remove marginBottom for fit to bottom */}
            <Grid container spacing={2} justifyContent="center">
              {buttons}
            </Grid>
            <Grid  className='smallimg' style ={{marginRight:'50px'}} >
        </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
    </Grid>

  );
}

export default UserHost;
