import  React,{useState,useEffect} from 'react';
import Grid from '@mui/material/Grid';
import IconButton from '@mui/material/IconButton';
import CallIcon from '@mui/icons-material/Call';
import bgimage from '../images/person.jpeg';
import '../ComponentStyle/Host.css';
import io from 'socket.io-client';
import WebSocket from 'ws';

const ws = new WebSocket('ws://localhost:3000');

const UserHost =()=> {

  const [timerValue, setTimerValue] = useState(null);

  function handleTimerClick(time) {
  setTimerValue(time);
  ws.send(time);
  }

    const buttons = [
      <IconButton key="1" aria-label="call" color="primary" style={{ backgroundColor: '#c4c4c4', borderRadius: '50%' ,margin: '10px'}}><CallIcon /></IconButton>,
      <IconButton key="2" aria-label="call" color="primary" style={{ backgroundColor: '#c4c4c4', borderRadius: '50%' ,margin: '10px'}}><CallIcon /></IconButton>,
      <IconButton key="3" aria-label="call" color="primary" style={{ backgroundColor: '#c4c4c4', borderRadius: '50%' ,margin: '10px'}}  onClick={() => handleTimerClick(15)}>15s</IconButton>,

    ];
  return (
    <Grid container style={{ height: '100vh' }}>
    <Grid container>
    {/* // color match krwana h */}
      <Grid item xs={2} style={{ background: '#fff', height: '100%',border: '1px solid white'}}> 
        <Grid item className='sidegrid' >
          <div className='participant'>Participant1</div>
        </Grid>
        <Grid item  className='sidegrid' >
          <div className='participant'>Participant2</div>
        </Grid>
        <Grid item  className='sidegrid' >
          <div className='participant'>Participant3</div>
        </Grid>
      </Grid>
      <Grid item xs={10} className='maingrid'>
        <Grid container style={{ height: '100%' }}>
          <Grid item xs={12} style={{ display: 'flex', justifyContent: 'center', alignItems: 'flex-end', marginBottom: 20 }}>
            {/* remove marginBottom for fit to bottom */}
            <Grid container spacing={2} justifyContent="center">
              {buttons}
            </Grid>
            <Grid item  className='smallimg' >
        </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
    </Grid>

  );
}

export default UserHost;
